export function init(e){}export function play(e){e.classList.add("_playing"),e.querySelector("video").play()}export function pause(e){e.classList.remove("_playing"),e.querySelector("video").pause()}export function stop(e){pause(e),e.querySelector("video").currentTime=0}export function enterFullScreen(e){e.classList.add("_fullscreen"),e.requestFullscreen()}export function exitFullScreen(e){e.classList.remove("_fullscreen"),document.exitFullscreen()}